mapper.select:
select * from tf_el_message t 
	
		t.USER_ID = #{userId}
		
			and t.MESSAGE_TYPE = #{code}
		
	 
	order by to_char(to_date(t.message_time,'yyyy-MM-dd HH24:mi:ss'),'yyyyMMddHH24miss') desc

mapper.update:
update tf_el_message set IS_POPUP = '0',IS_READ = '1'

	MESSAGE_ID in
		
			#{message.messageId}
