mapper.select:
select co.course_id,co.course_name from tf_el_course co, user_course_rela re 
where co.course_id = re.course_id
	  and re.user_id = #{userId} 
	  and re.rela_type = '1';

select re.class_id, cl.class_name,re.course_id,tc.course_name,tc.section_number from user_course_rela re, tf_el_course tc,tf_el_class cl
re.course_id = tc.course_id and re.class_id = cl.class_id and re.user_id = #{userId} and re.rela_type = '1'
and re.class_id = #{classId}

select re.section_id,ts.section_name from user_course_rela re, tf_el_section ts 
where re.section_id = ts.section_id 
  and re.rela_type = '0' 
  and re.user_id = #{userId}
  and re.course_id = #{courseId}
  and re.class_id = #{classId} 
  and re.isfinish = '2'

select ts.section_Id ts.section_name,ts.section_url,ns.class_id,ns.note,ns.summary 
	from tf_el_note_summary ns, tf_el_section ts 
where ns.section_id = ts.section_id 
	  and ns.user_id = #{con_userId} 
	  and ns.course_id = #{con_courseId} 
	  and ns.class_id = #{con_classId}

select count(1) from tf_el_note_summary t 
where t.USER_ID = #{userId} 
	and t.SECTION_ID = #{sectionId} 
	and t.class_id = #{classId}
	and t.SUMMARY is not null
