mapper.update:
update TF_EL_SUMMARY
set summary = #{summary}
where user_id = #{userId}
and course_id = #{courseId}
and section_sort = #{sectionSort}

mapper.select:
select count(1) from TF_EL_SUMMARY
where USER_ID = #{userId}
AND course_id = #{courseId}
and section_sort = #{sectionSort}

select * from TF_EL_SUMMARY
where USER_ID = #{userId}
AND course_id = #{courseId}
and section_sort = #{sectionSort}
