mapper.insert:
insert
when (
      not exists (
      select #{sectionSort} from TF_EL_SUMMARY
            where user_id = #{userId} and section_sort = #{sectionSort} and course_id = #{summary}
            )
      ) then
into TF_EL_SUMMARY(user_id, section_sort, course_id, summary) values(#{userId},#{sectionSort},#{summary},'')
select #{userId},#{sectionSort},#{summary} from dual

mapper.update:
update TF_EL_SUMMARY
set summary = #{summary}
where user_id = #{userId}
and course_id = #{courseId}
and section_sort = #{sectionSort}
