mapper.select:
select tu.USER_ID,tu.USER_NAME,tu.SERIAL_NUMBER,tu.ADMIN_FLAG,tu.end_Date,tu.START_TIME, tu.ISDEFAULT,tu.SCHOOL_ID,pa.pic_id,pa.pic_url,pa.save_path,s.SCHOOL_NAME from TF_EL_TEACH_USER tu,TF_EL_PIC_ATTR pa,TF_EL_SCHOOL s
tu.USER_ID = pa.ASSOCIATION_ID and tu.SCHOOL_ID = s.SCHOOL_ID
and tu.ADMIN_FLAG <> '0'
and tu.ADMIN_FLAG = #{adminFlag}
and tu.USER_NAME like '%'||#{userName}||'%'
and tu.SCHOOL_ID = #{schoolId}

select * from TF_EL_TEACH_USER t where t.USER_ID = #{id}
union
select * from TF_EL_TEACH_USER t where t.serial_number = #{id}

mapper.update:
update TF_EL_TEACH_USER t
	set t.USER_NAME = #{userName}, t.PASS_WORD = #{passWord}, t.ISDEFAULT = #{isDefault},
		t.MODIFIER = #{modifier}, t.UPDATE_TIME = #{update}
	where t.user_Id = #{userId}

mapper.select:
select user_id, user_name,SCHOOL_ID
from TF_EL_TEACH_USER
where SCHOOL_ID = #{schoolId}
